package com.sotogito.section04.casting;

public class Application1 {
    /**
     * ## 컴퓨터 값 처리 규칙
     * 1. 같은 자료형의 값만 대입 가능, 대입연산자를 기준으로 좌우 동일한 자료형
     * 2. 같은 타입끼리 연산, 결과 동일 타입
     * <p>
     * ## 형변환
     * 1. 자동(묵시적)형변환
     * 2. 강제(명시적)형변환
     */
    public static void main(String[] args) {
        /**
         * byte -> short -> int -> long -> float -> double
         * -> : 자동형변환
         * <- : 강제형변환
         */

        int i1 = 12;
        double d1 = i1; //12 -> 12.0
        byte b1 = (byte) d1;

        int i2 = 12;
        double d2 = 3.3;
        int result = (int) (i1 + d2);

        System.out.println(i2 + d2); //15.3


        int i3 = 1000;
        long l1 = i3;

        float f4 = 1.0f;


        long l5 = 1000;
        float f5 = l5;

        int num = 'A'; //65

        char ch = 65; //A

        double test = 199999997777777777777.;
        float testResult = (float) test;


        byte b11 = 1;
        byte b22 = 10;
        int i = b11 + b22;
        byte b3 = (byte) (b11 + b22);
    }
}
